import { Component } from '@angular/core';
import { AuthService } from '../../service/auth.service';
import { Router } from '@angular/router';
import { AppComponent } from '../../../app/app.component';
import { Observer } from 'rxjs';
@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
    public form:any ={};
    constructor(private AuthService: AuthService,private router: Router,private main:AppComponent) {}
    ngOnInit(): void {
    //this.register();
    }
    login() {

        const observer: Observer<any> = {
            next: response => {
              if(response.token){
                this.AuthService.cookie.storeCookie('token',response.token);
                this.router.navigate(['/admin']);
              }else{
                this.main.showMessage('Error :'+response.status,"Error Auth",'',1);
              }
            },
            error: error => {
              console.log('Error:', error.error);
              this.main.showMessage('Error :'+error.status,error.error.error,'',1);
            },
            complete: () => {
              console.log('Complete');
            }
          };

          this.AuthService.login(this.form).subscribe(observer);


      }
}
<section class="vh-100" style="background-color: #eee;">
    <div class="container h-100">
      <div class="row d-flex justify-content-center align-items-center h-100">
        <div class="col-lg-12 col-xl-11">
          <div class="card text-black" style="border-radius: 25px;">
            <div class="card-body p-md-5">
              <div class="row justify-content-center">
                <div class="col-md-10 col-lg-6 col-xl-5 order-2 order-lg-1">

                  <p class="text-center h1 fw-bold mb-5 mx-1 mx-md-4 mt-4">Sign in</p>

                  <form class="mx-1 mx-md-4">

                    <div class="d-flex flex-row align-items-center mb-4">
                      <i class="fas fa-envelope fa-lg me-3 fa-fw"></i>
                      <div class="form-outline flex-fill mb-0">
                        <input [(ngModel)]="form.email" type="email" id="form3Example3c" class="form-control"  [ngModelOptions]="{standalone: true}"/>
                        <label class="form-label" for="form3Example3c">Your Email</label>
                      </div>
                    </div>

                    <div class="d-flex flex-row align-items-center mb-4">
                      <i class="fas fa-lock fa-lg me-3 fa-fw"></i>
                      <div class="form-outline flex-fill mb-0">
                        <input [(ngModel)]="form.password" type="password" id="form3Example4c" class="form-control" [ngModelOptions]="{standalone: true}"/>
                        <label class="form-label" for="form3Example4c">Password</label>
                      </div>
                    </div>


                    <div class="d-flex justify-content-center mx-4 mb-3 mb-lg-4">
                      <button  (click)="login()" type="button" class="btn btn-primary btn-lg">Login</button>
                    </div>

                  </form>

                </div>
                <div class="col-md-10 col-lg-6 col-xl-7 d-flex align-items-center order-1 order-lg-2">

                  <img src="https://mdbcdn.b-cdn.net/img/Photos/new-templates/bootstrap-registration/draw1.webp"
                    class="img-fluid" alt="Sample image">

                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  </section>
